from buildbot.steps.shell import ShellCommand

class RunPreamble(ShellCommand):
    haltOnFailure = False
    flunkOnFailure = True
    name = "RunPreamble"

    def __init__(self, factory, argdict=None, **kwargs):
        self.description = "Running Preamble"
        self.factory = factory

        self.env_cmd = "./oe-init-build-env"
        for k, v in argdict.iteritems():
            setattr(self, k, v)

        self.command = ". " + self.env_cmd
        ShellCommand.__init__(self, **kwargs)

    def start(self):
        #TODO abort here if the command didn't work
        self.setProperty('env_cmd', self.env_cmd, "Setting environment command")
        ShellCommand.start(self)
